def calcApi(tokenAddress):
    apiURL = "https://api.pancakeswap.info/api/v2/tokens/"
    response = requests.get(url = apiURL + tokenAddress)
    price = extractPriceFromRaw(response)
    return price

def extractPriceFromRaw(response):
    jsonRaw = response.json()
    price = jsonRaw['data']['price']
    return price

def calcWeb3(tokenAddress):
    routerContract = web3.eth.contract(address=pancakeContractAddress, abi=panabi)
    oneToken = web3.toWei(1, 'Ether')
    price = routerContract.functions.getAmountsOut(oneToken, [web3.toChecksumAddress(tokenAddress), web3.toChecksumAddress(BUSD)]).call()
    normalizedPrice = web3.fromWei(price[1], 'Ether')
    return f'{normalizedPrice} BUSD'

